  )

mapper.select:
select id, open_id, goods_id, goods_attr, add_num, create_date,total_price
from t_cart
where id = #{id,jdbcType=INTEGER}

select id, open_id, goods_id, goods_attr, add_num, create_date,total_price
from t_cart
where open_id=#{openId,jdbcType=VARCHAR}

select sum(add_num)
from t_cart
