mapper.update:
update t_group_member
set group_id = #{groupId,jdbcType=INTEGER},
  group_child_id = #{groupChildId,jdbcType=INTEGER},
  open_id = #{openId,jdbcType=INTEGER},
  create_date = #{createDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, group_id, group_child_id, open_id, create_date
from t_group_member
where id = #{id,jdbcType=INTEGER}
