mapper.select:
	  select 

  from t_user
  where (account_name=#{userName,jdbcType=VARCHAR} or phone = #{userName,jdbcType=VARCHAR}) and password=#{pwd,jdbcType=VARCHAR}

select count(1) from t_user where 1=1

  and  to_days(create_date) = to_days(now());


  and  DATE_SUB(CURDATE(), INTERVAL 30 DAY)  <=  date(create_date)
