mapper.update:
update t_user_share
set open_id = #{openId,jdbcType=VARCHAR},
  goods_id = #{goodsId,jdbcType=INTEGER},
  share_image = #{shareImage,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, open_id, goods_id, share_image
from t_user_share
where id = #{id,jdbcType=INTEGER}
